const cookie = require('cookie');

module.exports = (request, config) => {
  const finalConfig = {};

  // CSRF配置生成
  const csrfConfig = {
    token: '_tb_token_',
  };

  if (typeof config.csrf === 'string' && config.csrf === true) {
    finalConfig.csrf = csrfConfig;
  } else if (typeof config.csrf === 'string' && config.csrf === false) {
    finalConfig.csrf = false;
  } else if (typeof config.csrf === 'object' && !!config.csrf.token) {
    finalConfig.csrf = Object.assign(csrfConfig, config.csrf);
  } else {
    finalConfig.csrf = false;
  }

  // 获取csrf标识
  if (finalConfig.csrf) {
    request.csrf = getCsrfToken();
    request.url += `&${finalConfig.csrf.token}=${request.csrf}`;
  }

  /**
   * 读取csrfToken
   * @returns {*}
   */
  function getCsrfToken() {
    let token = null;
    // 从cookie中读取相应值
    const cookies = cookie.parse(document.cookie);
    token = cookies[config.csrf.token];
    if (token) {
      return token;
    }
    return false;
  }
};

